import React from 'react'
import Product from './Product'


function Checkout() {

    const intialitem=()=>{

        const a= localStorage.getItem("mytodolist");
        if(a)
        {
            return JSON.parse(a);
        }
        else{
            return [];
        }
      
      }
    return (
        <div>
            `${intialitem()}`
            {intialitem().map((x)=>{
                return (<Product
                id={x.id}
                title={x.title}
                image={x.image}
                price={x.price}
                basket={x.basket}
                
                rating={x.rating}
                setbasket={x.setbasket}




                />);
            })}
        </div>
    )
}

export default Checkout
