import React,{useState,useEffect} from 'react';
import './App.css';
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import Home from './Home';
import Header from './Header';
import Checkout from './Checkout';


function App() {
  const [basket,setbasket]= useState([])
  useEffect(() => {

    localStorage.setItem("mytodolist",JSON.stringify(basket))

}, [basket])

const intialitem=()=>{

  const a= localStorage.getItem("mytodolist");
  if(a)
  {
      return JSON.parse(a);
  }
  else{
      return [];
  }

}

  return (

      <Router>
        <div className="App">
          <Header/>


        <Switch>

          <Route path="/Checkout">

                <Checkout basket={basket}/>
          </Route>

          <Route path="/" >

                <Home  basket={basket} setbasket={setbasket}  />
          </Route>




        </Switch>
        </div>
      </Router>

  );
}

export default App;
