import React ,{useState} from 'react'
import './Product.css'



function Product({ id, title, image, price, rating ,basket,setbasket}) {
    return (
        <div className="product">
            <div className="product__info">
                <p>{title}</p>
                <p className="product__price">
                    <small>$</small>
                    <strong>{price}</strong>

                </p>
                <div className="product__rating">
                   {Array(rating).fill().map(()=>{
                       return <p><i class="fa-solid fa-star"></i></p>;
                   })}
                </div>
            </div>

            <img src={image} alt="" />
            <button onClick={()=>{setbasket(['a','bs'])}} >Add to Basket</button>
      
        </div>
    )
}

export default Product
